integrationScript.script:
function guid() {
  function s4() {
    return Math.floor((1 + Math.random()) * 0x10000)
      .toString(16)
      .substring(1);
  }
  return s4() + s4() + '-' + s4() + '-' + s4() + '-' + s4() + '-' + s4() + s4() + s4();
}
var sync = params.sync;
function setLock(guid, info) {
    if (sync) {
        var versionedIntegrationContext = getVersionedIntegrationContext(true, true) || {};
        var integrationContext = versionedIntegrationContext.context;
        integrationContext[lockName] = {guid: guid, info: info};
        setVersionedIntegrationContext(integrationContext, true, versionedIntegrationContext.version);
    } else {
        var integrationContext = getIntegrationContext() || {};
        integrationContext[lockName] = {guid: guid, info: info};
        setIntegrationContext(integrationContext);
    }
} function getLock() {
    if (sync) {
        var versionedIntegrationContext = getVersionedIntegrationContext(true, true) || {};
        var integrationContext = versionedIntegrationContext.context;
        if (!integrationContext[lockName]) {
            integrationContext[lockName] = {};
        }
        return integrationContext[lockName];
    } else {
        var integrationContext = getIntegrationContext() || {};
        if (!integrationContext[lockName]) {
            integrationContext[lockName] = {};
        }
        return integrationContext[lockName];
    }
}
var lockName = args.name || 'Default';

switch (command) {
    case 'test-module':
        return 'ok';

    case 'demisto-lock-get':
        var lockTimeout = args.timeout || params.timeout;
        var lockInfo = 'Locked by incident #' + incidents[0].id + '.';
        lockInfo += (args.info) ? ' Additional info: ' + args.info :'';

        var guid = guid();
        var time = 0;
        var lock = getLock();

        while (lock.guid !== guid && time++ < lockTimeout) {
            wait(1);
            lock = getLock();
            if (lock.guid === guid) {
                continue;
            }
            if (!lock.guid) {
                try {
                    setLock(guid, lockInfo);
                } catch(err) {
                    logDebug(err.message)
                }
            }
        }

        if (lock.guid === guid) {
            var md = '### Demisto Locking Mechanism\n';
            md += 'Lock acquired successfully\n';
            md += 'GUID: ' + guid;
            return { ContentsFormat: formats.markdown, Type: entryTypes.note, Contents: md } ;
        } else {
            var md = 'Timeout waiting for lock\n';
            md += 'Lock name: ' + lockName + '\n';
            md += 'Lock info: ' + lock.info + '\n';
            return { ContentsFormat: formats.text, Type: entryTypes.error, Contents: md };
        }
        break;

    case 'demisto-lock-release':
        integrationContext = getVersionedIntegrationContext(sync);
        integrationContext[lockName] = {};
        setVersionedIntegrationContext(integrationContext, sync);

        var md = '### Demisto Locking Mechanism\n';
        md += 'Lock released successfully';
        return { ContentsFormat: formats.markdown, Type: entryTypes.note, Contents: md } ;

    case 'demisto-lock-release-all':
        setVersionedIntegrationContext({}, sync);

        var md = '### Demisto Locking Mechanism\n';
        md += 'All locks released successfully';
        return { ContentsFormat: formats.markdown, Type: entryTypes.note, Contents: md } ;

    case 'demisto-lock-info':
        integrationContext = getVersionedIntegrationContext(sync);
        var obj = [];

        var res;
        var md = '### Demisto Locking Mechanism\n';
        var locks = (lockName === 'Default') ? Object.keys(integrationContext) : [lockName];

        locks.forEach(function(lock){
            md += 'Lock name: ' + lock + ' - ';
            if (integrationContext[lock] && integrationContext[lock].guid) {
                md += 'Locked.\n';
                md += '- GUID: ' + integrationContext[lock].guid + '\n';
                md += '- Info: ' + integrationContext[lock].info + '\n\n';
                obj.push({lock: lock, state: integrationContext[lock]});
            } else {
                md += 'Not locked\n\n';
            }

        });
        return { ContentsFormat: formats.json, Type: entryTypes.note, Contents: obj, HumanReadable: md } ;

    default:
        var md = 'Unknown command ' + command;
        return { ContentsFormat: formats.text, Type: entryTypes.error, Contents: md };
}

var serverURL = params.url;
if (serverURL.slice(-1) === '/') {
    serverURL = serverURL.slice(0,-1);
}

sendMultipart = function (uri, entryID, body) {
    var requestUrl = serverURL;
    if (uri.slice(-1) !== '/') {
        requestUrl += '/';
    }
    requestUrl += uri;

    var res = httpMultipart(
        requestUrl,
        entryID,
        {
            Headers: {
                'Authorization': [params.apikey],
                'Content-Type': ['multipart/form-data'],
                'Accept': ['application/json']
            },
        },
        body,
        params.insecure,
        params.proxy,
        undefined,
        'file'
    );
    if (res.StatusCode < 200 || res.StatusCode >= 300) {
        throw 'Demisto REST APIs - Request Failed.\nStatus code: ' + res.StatusCode + '.\nBody: ' + JSON.stringify(res) + '.';
    }
    try {
        var response = res.Body;
        try {
            response = JSON.parse(res.Body);
        } catch (ex) {
            // do nothing, already handled prior the try/catch
        }
        return {response: response};
    } catch (ex) {
        throw 'Demisto REST APIs - Error parsing response - ' + ex + '\nBody:' + res.Body;
    }

};

var sendRequest = function(method, uri, body, raw) {
    var requestUrl = serverURL;
    if (uri.slice(0, 1) !== '/') {
        requestUrl += '/';
    }
    requestUrl += uri;

    var res = http(
        requestUrl,
        {
            Method: method,
            Headers: {
                'Accept': ['application/json'],
                'content-type': ['application/json'],
                'authorization': [params.apikey]
            },
            Body: body,
            SaveToFile: raw
        },
        params.insecure,
        params.proxy
    );

    if (res.StatusCode < 200 || res.StatusCode >= 300) {
        throw 'Demisto REST APIs - Request Failed.\nStatus code: ' + res.StatusCode + '.\nBody: ' + JSON.stringify(res) + '.';
    }
    if (raw) {
        return res;
    } else {
        try {
            var response = res.Body;
            try {
                response = JSON.parse(res.Body);
            } catch (ex) {
                // do nothing, already handled prior the try/catch
            }
            return {response: response};
        } catch (ex) {
            throw 'Demisto REST APIs - Error parsing response - ' + ex + '\nBody:' + res.Body;
        }
    }
};

var deleteIncidents = function(ids_to_delete) {
    var body = {
        ids: ids_to_delete,
        all: false,
        filter: {}
    };

    var res = sendRequest('POST', '/incident/batchDelete', JSON.stringify(body));
    if (isError(res[0])) {
        throw res[0].Contents;
    }

    var response = res['response']
    var md = tableToMarkdown('Demisto delete incidents', response, ['data', 'total', "notUpdated"]);

    return {
        ContentsFormat: formats.json,
        Type: entryTypes.note,
        Contents: res,
        HumanReadable: md
    };
};

switch (command) {
    case 'test-module':
        sendRequest('GET','user');
        return 'ok';
    case 'demisto-api-post':
        var body = JSON.parse(args.body);
        return sendRequest('POST',args.uri, args.body);
    case 'demisto-api-get':
        return sendRequest('GET',args.uri);
    case 'demisto-api-put':
        var body = JSON.parse(args.body);
        return sendRequest('PUT',args.uri, args.body);
    case 'demisto-api-delete':
        return sendRequest('DELETE',args.uri);
    case 'demisto-api-multipart':
        return sendMultipart(args.uri, args.entryID, args.body);
    case 'demisto-api-download':
        var res = sendRequest('GET',args.uri,args.body,true);
        var filename = res.Path;
        if (args.filename) {
            filename = args.filename;
        } else {
            var disposition = res.Headers['Content-Disposition'][0].split('=');
            if (disposition.length === 2) {
                filename = disposition[1];
            }
        }
        var desc = args.description || '';
        return ({Type: entryTypes.file, FileID: res.Path, File: filename, Contents: desc});
    case 'demisto-delete-incidents':
        var ids = argToList(args.ids);
        return deleteIncidents(ids);
    default:
        throw 'Demisto REST APIs - unknown command';
}

import subprocess
import traceback
from typing import List

TESSERACT_EXE = 'tesseract'


def list_languages() -> List[str]:
    lang_out = subprocess.check_output([TESSERACT_EXE, '--list-langs'], universal_newlines=True)
    if not lang_out:  # something went wrong
        raise ValueError('No output from --list-langs')
    lines = lang_out.splitlines()
    if len(lines) <= 1:
        raise ValueError('No output from --list-langs')
    return sorted(lines[1:])  # ignore first line


def extract_text(image_path: str, languages: List[str] = None) -> str:
    exe_params = [TESSERACT_EXE, image_path, 'stdout']
    if languages:
        exe_params.extend(['-l', '+'.join(languages)])
    res = subprocess.run(exe_params, capture_output=True, check=True, text=True)
    if res.stderr:
        demisto.debug('tesseract returned ok but stderr contains warnings: {}'.format(res.stderr))
    return res.stdout


def list_languages_command() -> CommandResults:
    langs = list_languages()
    return CommandResults(
        readable_output="## Image OCR Supported Languages\n\n" + "\n".join(['* ' + s for s in langs]),
        raw_response=langs
    )


def extract_text_command(args: dict, instance_languages: list) -> CommandResults:
    langs = argToList(args.get('langs')) or instance_languages
    demisto.debug("Using langs settings: {}".format(langs))

    entry_id = args.get('entryid')
    file_path = demisto.getFilePath(entry_id)
    if not file_path:
        raise DemistoException("Couldn't find entry id: {}".format(entry_id))

    demisto.debug('Extracting text from file: {}'.format(file_path))
    res = extract_text(file_path['path'], langs)
    file_entry = {'EntryID': entry_id, 'Text': res}

    return CommandResults(
        readable_output="## Image OCR Extracted Text\n\n" + res,
        outputs_prefix='File',
        outputs_key_field='EntryID',
        outputs=file_entry,
        raw_response=res
    )


def run_test_module(instance_languages: list) -> str:
    try:
        supported_langs = list_languages()
        if instance_languages:
            for language in instance_languages:
                if language not in supported_langs:
                    raise DemistoException('Unsupported language configured: {}'.format(language))
        return 'ok'
    except Exception as exception:
        raise Exception('Failed testing {}: {}'.format(TESSERACT_EXE, str(exception)))


def main() -> None:
    command = demisto.command()
    args = demisto.args()
    instance_languages = argToList(demisto.params().get('langs'))
    try:
        if command == 'test-module':
            return_results(run_test_module(instance_languages))
        elif command == 'image-ocr-list-languages':
            return_results(list_languages_command())
        elif command == 'image-ocr-extract-text':
            return_results(extract_text_command(args, instance_languages))
        else:
            raise NotImplementedError(f'Command {command} was not implemented.')
    except subprocess.CalledProcessError as cpe:
        return_error("Failed {} execution. Return status: {}.\nError:\n{}".format(cpe.cmd, cpe.returncode, cpe.stderr))
    except Exception as err:
        return_error("Failed with error: {}\n\nTrace:\n{}".format(str(err), traceback.format_exc()))


# python2 uses __builtin__ python3 uses builtins
if __name__ in ("__builtin__", "builtins", '__main__'):
    main()

//GLOBALS//
var serverUrl = params.address;
if (serverUrl[serverUrl.length - 1] !== '/') {
    serverUrl += '/';
}

var EPO_SYSTEM_ATTRIBUTE_MAP = {
    'EPOComputerProperties.ComputerName': 'Name',
    'EPOComputerProperties.DomainName': 'Domain',
    'EPOComputerProperties.IPHostName': 'Hostname',
    'EPOComputerProperties.IPAddress': 'IPAddress',
    'EPOComputerProperties.OSType': 'OS',
    'EPOComputerProperties.OSVersion': 'OSVersion',
    'EPOComputerProperties.CPUType': 'Processor',
    'EPOComputerProperties.NumOfCPU': 'Processors',
    'EPOComputerProperties.TotalPhysicalMemory': 'Memory',
};

//HELPER//
function callEpo(pCommand, pArgs) {
    var url = serverUrl + 'remote/' + pCommand;
    url += encodeToURLQuery(pArgs);
    url += (Object.keys(pArgs).length > 0) ? '&' : '?';
    url += ':output=json';
    var result = http(
        url,
        {
            Method: 'GET',
            Username: params.authentication.identifier,
            Password: params.authentication.password
        },
        params.insecure,
        params.proxy
    );

    if (isResponseOK(result)) {
        trimmedRes = result.Body.substring(okStr.length);
        trimmedRes = trimmedRes.replace('\n',' ');
        parsedRes = JSON.parse(trimmedRes);
        return parsedRes;
    } else if (result.StatusCode == 200){
        throw 'Error accessing ePO interface. Command: ' + url + ". Body: " + result.Body;
    } else {
        throw 'Error accessing ePO interface. Command: ' + url + ". Response: " + JSON.stringify(result);
    }
}

function doRawHttp(method, url, parameters, body) {
    if (!parameters) {
        parameters = {};
    }
    var result = http(
        url + encodeToURLQuery(parameters),
        {
            Headers: {'Content-Type': ['application/json']},
            Method: method,
            Body: ''
        },
        params.insecure,
        params.proxy
    );
    if (result.StatusCode < 200 && result.StatusCode > 299) {
        throw 'Failed to perform request ' + url + encodeToURLQuery(parameters) + ', request status code: ' + result.StatusCode;
    }
    return result;
}

function isResponseOK(response) {
    // EPO response if of format: OK -> data. Error: error string
    if (response.StatusCode === 200) {
        okStr = 'OK:';
        if (response.Body.indexOf(okStr) === 0) {
            return true;
        }
    }
    return false;
}

function systemsToMd(systems,verbose) {
    var i;
    var md='';
    if (verbose=="true") {
        for (i=0;i<systems.length;i++) {
            md += systemToMd(systems[i],verbose);
        }
    } else {
        var tmpHead='|';
        var tmpLine='|';
        for (var key in EPO_SYSTEM_ATTRIBUTE_MAP) {
            tmpHead += EPO_SYSTEM_ATTRIBUTE_MAP[key] + '|';
            tmpLine += '-|';
        }
        md += tmpHead + '\n'+tmpLine+'\n';
        for (i=0;i<systems.length;i++) {
            md += systemToMd(systems[i],verbose);
        }
    }
    return md;
}

function systemToMd(system,verbose) {
    var md='';
    var key;
    if (verbose=="true") {
        md += '#### '+ system["EPOComputerProperties.ComputerName"]+'\n';
        md += "Attribute|Value\n-|-\n";
        for (key in system) {
            md += key +"|"+system[key]+"\n";
        }
        md += "---\n";
    } else {
        md +='|';
        for (key in EPO_SYSTEM_ATTRIBUTE_MAP) {
            md += system[key] +"|";
        }
        md += '\n';
    }
    return md;
}

//COMMANDS//
function epoHelp() {
    var pArgs = {};
    if (args.command) {
        pArgs.command = args.command;
    }
    var readyRes = callEpo('core.help',pArgs);
    var md;
    var line;
    var cmd;
    var desc;

    if (args.command) {
        md = '#### ePO Help - ' + args.command + '\n';
        md += readyRes;
    } else {
        parsedRes = Array.isArray(parsedRes) ? parsedRes : [parsedRes];
        md = '#### ePO Help\n';
        for (var i=0;i<parsedRes.length;i++) {
            line = parsedRes[i];
            line = line.replace(/(\r\n|\n|\r)/gm," ");
            if (!args.search || (args.search.toLowerCase() && line.toLowerCase().indexOf(args.search.toLowerCase())>=0)) {
                desc = line.split('-')[1] ? line.split('-')[1].trim() : 'N/A';
                cmd = line.split('-')[0] ? line.split('-')[0].trim() : 'N/A';
                md += '- **' + cmd + "** - " + desc + '\n';
            }
        }
    }
    return {
        Type: entryTypes.note,
        Contents: readyRes,
        ContentsFormat: formats.json,
        HumanReadable: md
    };
}

function epoGetLatestDatRaw() {
    var url = "http://update.nai.com/products/commonupdater/gdeltaavv.ini"
    //Another option is to use avvdat.ini - provided here in case any additional data from that file is needed in the future
    //var url = "http://update.nai.com/Products/CommonUpdater/avvdat.ini"
    result = doRawHttp("GET",url);
    var res = '';
    var sections = result.Body.split('\r\n\r\n');
    var lines = sections[0].split('\r\n');
    for (var i=0;i<lines.length;i++) {
        if (lines[i].indexOf("CurrentVersion")===0){
            return lines[i].split('=')[1];
        }
    }
    throw 'Cannot get McAfee latest DAT file version';
}

function epoGetLatestDat() {
    var datVersion = epoGetLatestDatRaw();
    var md='McAfee Latest DAT file version is: **' + datVersion + '**\n';
    ec = {};
    ec.mcafee = {"latestDAT": datVersion};
    return {
        Type: entryTypes.note,
        Contents: datVersion,
        ContentsFormat: formats.json,
        HumanReadable: md,
        EntryContext: {
                "mcafee(val.latestDAT === obj.latestDAT)": ec.mcafee, //backward compatability
                "McAfee.ePO.latestDAT": datVersion
        }
    };
}

function epoGetCurrentDat() {
    var pArgs = {"searchText":"VSCANDAT1000"};
    var result = callEpo('repository.findPackages', pArgs);
    if (result && result.length>0) {
        result = result[0];
        var epoDAT = result.productDetectionProductVersion.split('.')[0];
        ec = {};
        ec.mcafee = {"epoDAT": epoDAT};
        var md = "McAfee ePO Current DAT file version in repository is: **" + epoDAT + '**\n';
        return {
            Type: entryTypes.note,
            Contents: result,
            ContentsFormat: formats.json,
            HumanReadable: md,
            EntryContext: {
                "mcafee(val.epoDAT==obj.epoDAT)": ec.mcafee, //backward compatability
                "McAfee.ePO.epoDAT": epoDAT
            }
        };
    } else {
        throw 'Error getting current DAT version. Please check that the DAT file exists. Response: ' + JSON.stringify(result);
    }
}

function epoUpdateClientDatTask() {
    var pArgs = {'searchText': 'VSEContentUpdateDemisto'}; //need to configure this task in ePO beforehand
    var result = callEpo('clienttask.find', pArgs);
    if (result && Array.isArray(result)) {
        result = result[0];
    }
    if (result) {
          return result.objectId;
        }
         // If reached here then the response is an empty "OK:", which means VSEContentUpdateDemisto was not found in the server
        throw 'Error getting DAT update task. It seems the task "VSEContentUpdateDemisto" is missing from the EPO server. Please contact support for more details';
}

function epoUpdateClientDat() {
    var clientTask = epoUpdateClientDatTask();
    var pArgs = {'names':args.systems, 'productId':'EPOAGENTMETA', 'taskId':clientTask};

    var options = ['retryAttempts', 'retryIntervalInSeconds', 'abortAfterMinutes', 'stopAfterMinutes', 'randomizationInterval'];
    for (var i=0;i<options.length;i++) {
        if (args[options[i]]) {
            pArgs[options[i]] = args[options[i]];
        }
    }
    var result = callEpo('clienttask.run', pArgs);
    var md = "ePO client DAT update task started: " + result;
    return {
        Type: entryTypes.note,
        Contents: result,
        ContentsFormat: formats.json,
        HumanReadable: md
    };
}

function epoUpdateRepository() {
    var pArgs = {
        'sourceRepository': 'McAfeeHttp',
        'targetBranch':'Current'
    };
    var result = callEpo('repository.pull', pArgs);

    var md = "ePO repository update started.\n";
    md += result;
    return {
        Type: entryTypes.note,
        Contents: result,
        ContentsFormat: formats.json,
        HumanReadable: md
    };
}

function prettifySystemTree(SystemTree) {
    var prettySystemTree = [];
    for (i=0; i < SystemTree.length; i++) {
        var system = SystemTree[i];
        prettySystemTree[i] = {
            'groupId': system.groupId,
            'groupPath': system.groupPath
        };
    }
    return prettySystemTree;
}

function epoGetSystemTreeGroups() {
    var pArgs = {};
    if (args.search) {
        pArgs.searchText=args.search;
    }
    var result = callEpo('system.findGroups', pArgs);
    if (result.length === 0) {
        return 'System Tree Group was not found.';
    }
    var md = "#### ePO System Tree groups\n";
    md += "Group ID | Group path\n-|-\n";
    for (var i=0;i<result.length;i++) {
        md += result[i].groupId + "|" + result[i].groupPath + "\n";
    }
    return {
        Type: entryTypes.note,
        Contents: result,
        ContentsFormat: formats.json,
        HumanReadable: md,
        EntryContext: {
            "McAfee.ePO(val.groupId==obj.groupId)": {'SystemTreeGroups': prettifySystemTree(result)}
        }
    };
}

function epoGetSystemGroupPath(groupId) {
    var result = epoGetSystemTreeGroups();
    result = result.Contents;
    for (var i=0;i<result.length;i++) {
        if (groupId == result[i].groupId) {
            return result[i].groupPath;
        }
    }
    return null;
}

function prettifyFindSystem(FindSystem) {
    var prettyFindSystem = [];
    for (i=0; i < FindSystem.length; i++) {
        var system = FindSystem[i];
        prettyFindSystem[i] = {
            'Name': system['EPOComputerProperties.ComputerName'],
            'Domain': system['EPOComputerProperties.DomainName'],
            'Hostname': system['EPOComputerProperties.IPHostName'],
            'IPAddress': system['EPOComputerProperties.IPAddress'],
            'OS': system['EPOComputerProperties.OSType'],
            'OSVersion': system['EPOComputerProperties.OSVersion'],
            'Processor': system['EPOComputerProperties.CPUType'],
            'Processors': system['EPOComputerProperties.NumOfCPU'],
            'Memory': system['EPOComputerProperties.TotalPhysicalMemory'],
        };
    }
    return prettyFindSystem;
}

function epoFindSystems() {
    var name = epoGetSystemGroupPath(args.groupId);

    if (!name) {
        throw 'System Tree group not found';
    }
    var pArgs = {'groupId':args.groupId};
    var result = callEpo('epogroup.findSystems', pArgs);
    if (result) {
        var md = '#### Systems in '+name+'\n';
        if (result.length>0) {
            md += systemsToMd(result,args.verbose);
        } else {
            md += 'No systems found\n';
        }
        return {
            Type: entryTypes.note,
            Contents: result,
            ContentsFormat: formats.json,
            HumanReadable: md,
            EntryContext: {
                'Endpoint(val.IPAddress==obj.IPAddress)': prettifyFindSystem(result),
                'McAfee.ePO.Endpoint(val.IPAddress==obj.IPAddress)': prettifyFindSystem(result),
            }
        };
    } else {
        throw 'No systems found. Response: ' + JSON.stringify(result);
    }
}

function epoFindSystem() {
    var pArgs = {'searchText':args.searchText};
    var result = callEpo('system.find', pArgs);
    if (result) {
        var md = '#### Systems in the System Tree\n';
        if (result.length>0) {
            md += systemsToMd(result,args.verbose);
        } else {
            md += 'No systems found\n';
        }
        return {
            Type: entryTypes.note,
            Contents: result,
            ContentsFormat: formats.json,
            HumanReadable: md,
            EntryContext: {
                'Endpoint(val.IPAddress==obj.IPAddress)': prettifyFindSystem(result),
                'McAfee.ePO.Endpoint(val.IPAddress==obj.IPAddress)': prettifyFindSystem(result),
            }
        };
    } else {
        throw 'No systems found. Response: ' + JSON.stringify(result);
    }
}

function epoWakeupAgent() {
    var pArgs = {'names':args.names};
    var result = callEpo('system.wakeupAgent', pArgs);

    var md = "ePO agent was awaken.\n";
    md += result;

    return {
        Type: entryTypes.note,
        Contents: result,
        ContentsFormat: formats.json,
        HumanReadable: md
    };
}

function epoApplyTag() {
    var pArgs = {
        'names': args.names,
        'tagName': args.tagName
    };
    var result = callEpo('system.applyTag', pArgs);

    var md = "ePO applied the tags on the hostnames successfully.\n";

    return {
        Type: entryTypes.note,
        Contents: result,
        ContentsFormat: formats.json,
        HumanReadable: md
    };
}

function epoClearTag() {
    var pArgs = {
        'names': args.names,
        'tagName': args.tagName
