dataset = panw_ngfw_traffic_raw 
| filter ((app not contains """palo""" and app not contains """xdr""" and app not contains """traps"""))
| alter in_range = if(incidr(dest_ip,"10.0.0.0/8") = true, "Inbound",
  if(incidr(dest_ip,"172.16.0.0/12") = true, "Inbound",
  if(incidr(dest_ip,"192.168.0.0/16") = true, "Inbound",
  if(incidr(dest_ip,"169.254.0.0/16") = true, "Inbound",
  if(incidr(dest_ip,"127.0.0.0/8") = true, "Inbound",
  if(incidr(dest_ip,"100.64.0.0/10") = true, "Inbound",
  if(dest_ip contains "fd00::") = true, "Inbound",
  if(dest_ip contains "fe80::") = true, "Inbound", "Outbound"))))))
| filter in_range = "Outbound"
| filter action = "allow"
| comp values(source_ip) as source_ips, values(dest_ip) as dest_ips, values(dest_location) as dest_locations, count(_id) as event_count, sum(to_integer(bytes_total)) as TotalBytes by _time, app, app_category
| sort desc TotalBytes

